import express from "express"
import dotenv from "dotenv"
import RedisStore from "connect-redis"
import session from "express-session"
import {createClient} from "redis"
import configViewEngine from "./config/viewEngine"
import initWebRoute from "./router/webRoute"
import initAPIRoute from "./router/apiRoute"
import path from "path"
import bodyParser from "body-parser"
import bcrypt from 'bcryptjs' // thư viện mã hóa mật khẩu
import JWT from "./middleware/JWTAction"
import Auth from "./middleware/Auth"

const app = express()

dotenv.config() // cấu hình port trong .env
const port = process.env.PORT

// Initialize client.
let redisClient = createClient()
redisClient.connect().catch(console.error)

// Initialize store.
let redisStore = new RedisStore({
  client: redisClient,
  prefix: "myapp:",
})

// Initialize sesssion storage.
app.use(
  session({
    store: redisStore,
    resave: false, // required: force lightweight session keep alive (touch)
    saveUninitialized: false, // recommended: only save session when data exists
    secret: "keyboard cat",
  })
)
app.use(bodyParser.urlencoded({ extended: false }))
app.use(bodyParser.json())

configViewEngine(app) // khởi tạo viewEngine

app.use('/static', express.static(path.join(__dirname,'public'))) // thêm "/static" vào link ảnh vd:"/static/img/h1.jpg"


// app.use("/create_new_user", Auth.Author)
// app.use("/insert_new_user", Auth.Author)
// app.use("/detail_user/:username", Auth.Author)
// app.use("/del_user", Auth.Author)
// app.use("/view_edit/:username", Auth.Author)
// app.use("/edit_user", Auth.Author)

Auth.authMiddleware(app)


initWebRoute(app) // khởi tạo router
initAPIRoute(app) // khởi tạo router api

app.use(Auth.Notfound)

// let payload = { foo: 'bar' }
// let code = JWT.createJWT(payload)
// JWT.verifyToken(code)

app.listen(port, () => {
  console.log(`Example app listening on port http://localhost:${port}`)
})

