                }
                else {
                    console.log("sai mat khau")
                }
            }

        } catch (error) {
            console.log(error)
        }
    }
}

const logout = (req, res) => {
    req.session.destroy()
    res.redirect('/login')
}

export default {login, login_auth, logout}

