import express from "express"


const Notfound = (req, res, next) => {
    res.status(404).send("<h1>Page not found on the server</h1>")
}

const Author = (req, res, next) => {

    if (req.session.auth) {
        next()
    }
    else {
        res.redirect('/login')
    }
}

const Authen = (req, res, next) => {

    if (req.session.auth && req.session.auth.role == 2) {
        next()
    }
    else {
        res.redirect('/login')
    }
}

const auth = express.Router()
const authMiddleware = (app) => {

    auth.use("/create_new_user", Authen)
    auth.use("/insert_new_user", Authen)
    auth.use("/detail_user/:username", Author)
    auth.use("/del_user", Authen)
    auth.use("/view_edit/:username", Authen)
    auth.use("/edit_user", Authen)

    return app.use("/", auth)
}

export default {authMiddleware, Notfound}

