import express from "express"
import bcrypt from 'bcryptjs'
import userModel from "../services/userModel"
import { json } from "body-parser"
import JWT from "../middleware/JWTAction"


const login = (req, res) => {

    try {

        let auth = null
    
        if (req.session.auth) {
            auth = req.session.auth.name
        }
        
        return res.status(200).json(
            JWT.createJWT({data: {
                page: 'login', 
                title: 'Đăng nhập', 
                auth_name:auth}}
            )   
        )

    } catch (error) {
        return res.status(400).json(
            JWT.createJWT({data: {
                err: 1,
                mess: "Lỗi: " + error}}
            )
        )
    }
}

const login_auth = async (req, res) => {

    try {

        let body = req.body
        // console.log(body)

        if (!body.username || !body.password) {
            return res.status(400).json(
                JWT.createJWT({data: {
                    err: 1,
                    mess: "Lỗi: thiếu tài khoản hoặc mật khẩu"}}
                )  
            )
        }

        let auth = await userModel.auth(body.username)

        if (auth.length < 1) {
            return res.status(400).json(
                JWT.createJWT({data: {
                    err: 1,
                    mess: "Lỗi: tài khoản không tồn tại"}}
                ) 
            )
        }

        let hash = bcrypt.hashSync(body.password, bcrypt.genSaltSync(10))
            
        if (bcrypt.compareSync(body.password, auth[0].password)) {
            req.session.auth = {name:auth[0].username, pass:auth[0].password, role:auth[0].groupid}
            return res.status(200).json(
                JWT.createJWT({data: {
                    err: 0,
                    mess: "Đăng nhập thành công"}}
                ) 
            )
        }
        else {
            return res.status(400).json(
                JWT.createJWT({data: {
                    err: 1,
                    mess: "Lỗi: sai mật khẩu"}}
                ) 
            )
        }

    } catch (error) {
        return res.status(400).json(
            JWT.createJWT({data: {
                err: 1,
                mess: "Lỗi: " + error}}
            )
        )
    }
}

const logout = (req, res) => {

    try {

        if (req.session.auth) {
            req.session.destroy(auth)
            return res.status(200).json(
                JWT.createJWT({data: {
                    err: 0,
                    mess: "Đăng xuất thành công"}}
                )
            )
        }

        return res.status(400).json(
            JWT.createJWT({data: {
                err: 1,
                mess: "Chưa đăng nhập"}}
            )
        )        

    } catch (error) {
        return res.status(400).json(
            JWT.createJWT({data: {
                err: 1,
                mess: "Lỗi: " + error}}
            )
        )
    }
}

export default {login, login_auth, logout}

