        )

    } catch (error) {
        return res.status(400).json(
            JWT.createJWT({data: {
                err: 1,
                mess: "Lỗi: " + error}}
            )
        )
    }
    
}

const edit_user = async (req, res) => {

    try {

        let role = 1
        let body = req.body

        if (!body.name || !body.pass || !body.nameuser || !body.address || !body.sex || !body.email) {
            return res.status(400).json(
                JWT.createJWT({data: {
                    err: 1,
                    mess: "Thiếu thông tin cần thiết"}}
                )
            )
        }

        let pass = bcrypt.genSaltSync(10)
        let hash = bcrypt.hashSync(body.pass, pass)

        await userModel.edit_user(body.name, hash, body.nameuser, body.address, body.flexRadioDefault, body.email, role)

        return res.status(200).json(
            JWT.createJWT({data: {
                err: 0,
                mess: "Cập nhật thành công"}}
            )
        )

    } catch (error) {
        return res.status(400).json(
            JWT.createJWT({data: {
                err: 1,
                mess: "Lỗi: " + error}}
            )
        )
    }
}

const view_edit = async (req, res) => {

    try {

        let Username = req.params.username
        let list = await userModel.detail_user(Username)

        if (list.length < 1) {
            return res.status(400).json(
                JWT.createJWT({data: {
                    err: 1,
                    mess: "Người dùng không tồn tại"}}
                )
            )
        }

        return res.status(200).json(
            JWT.createJWT({data: {
                page: 'viewEdit', 
                title: 'Cập nhật', 
                list:list}}
            )
        )

    } catch (error) {
        return res.status(400).json(
            JWT.createJWT({data: {
                err: 1,
                mess: "Lỗi: " + error}}
            )
        )
    }

}

const del_user = async (req, res) => {

    try {

        let body = req.body

        if (!body.username) {
            return res.status(400).json(
                JWT.createJWT({data: {
                    err: 1,
                    mess: "Lỗi: thiếu tài khoản"}}
                )
            )
        }

        let list = await userModel.del_user(body.username)
        return res.status(200).json(
            JWT.createJWT({data: {
                err: 0,
                mess: "Xóa tài khoản thành công"}}
