    } catch (error) {
        return res.status(400).json(
            JWT.createJWT({data: {
                err: 1,
                mess: "Lỗi: " + error}}
            )
        )
    }
}

export default {new_user, list_user, detail_user, insert_user, edit_user, del_user, view_edit}

